import React, { useEffect, useState } from "react";
import LanguageBtn from "./LanguageBtn";
import compStyle from "./styles/LangBtnController.module.scss";

const LangBtnController = (props) => {
  type TBtnArrayWithPressedState = {
    btns: [JSX.Element, boolean];
  };

  const [activeBtnPosition, setActiveBtnPosition] = useState<Number>();
  const [flatBtns, setFlatBtns] = useState<Array<boolean>>([])
  console.log(activeBtnPosition);
  const forceUpdate = React.useReducer(() => ({}), {})[1] as () => void;

  function activeBtnSwitcher(Btns: { btns: [JSX.Element, boolean] }[]) {
    let flatBtns = Btns.flat().filter((ele) => typeof ele != "object");
    console.log(flatBtns);
    setActiveBtnPosition(flatBtns.indexOf(true));
    flatBtns.pop();
    if (activeBtnPosition == flatBtns.length) {
      flatBtns.unshift(true);
    } else {
      flatBtns.unshift(false);
    }
    console.log(flatBtns, "after manipulation");
    Btns.forEach((btn, index) => (btn[1] = flatBtns[index]));
    forceUpdate();
  }

  useEffect(() => {
    console.log("Heello from arrow");
    Btns.forEach((button, index) => {
        // button[1] = flatBtns
    })
  }, [activeBtnPosition]);

  const [activeBtn, setActiveBtn] = useState<string>(props.active);
  /**
   * Sets currently active button.
   * @param id of a button
   */
  const activeBtnSetter = (id: string) => setActiveBtn(id);

  const handleArrowClick = () => {
    activeBtnSwitcher(Btns);
    console.log(Btns);
  };
  let arrowBtn: HTMLElement | null;
  useEffect(() => {
    console.log(document.getElementById("arrowButton"), "found it");
    arrowBtn = document.getElementById("arrowButton");
    arrowBtn?.addEventListener("click", handleArrowClick);
    return () => arrowBtn?.removeEventListener("click", handleArrowClick);
  });

  // * Cleans other buttons from the pressed class.
  useEffect(() => {
    Btns.forEach((button) => {
      console.log(button[0].props.btnID);
      if (button[0].props.btnID !== activeBtn) {
        button[1] = false;
      }
    });
    console.log(Btns);
  }, []);

  const Btns: Array<TBtnArrayWithPressedState> = props.BtnArray.map(
    (button, key) => {
      return [
        <LanguageBtn
          btnID={button}
          btnText={button.toUpperCase()}
          handleBtnClick={props.clickHandler}
          inputEnabler={props.setIsNameEntryEnabled}
          pressed={button === activeBtn ? true : false}
          key={key}
          langSelect={props.langSelector}
          setActiveBtn={activeBtnSetter}
        />,
        button === activeBtn ? true : false,
      ];
    }
  );

  return (
    <div className={compStyle.LangSelect}>{Btns.map((ele: any) => ele)}</div>
  );
};

export default LangBtnController;
