import React, { useEffect, useState } from "react";
import Carousel from "react-multi-carousel";
// import "react-multi-carousel/lib/styles.css";

import compStyle from "./styles/PagesController.module.scss";

const PagesController = async (props: { currentPages: number }) => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  const [pagesAmount, setPagesAmount] = useState<Array<number>>([
    ...Array(props.currentPages).keys(),
  ]);

  useEffect(() => {
    setPagesAmount([...Array(props.currentPages).keys()]);
  }, [props.currentPages]);

  return (
    <div className={compStyle.BGContainer}>
      <Carousel
        containerClass={compStyle.Carousel}
        itemClass={`width: 160px`}
        responsive={responsive}
        showDots
        ssr
      >
        {
          await pagesAmount.map((page) => {
            return (
              <div>
                <span>{page + 1}</span>
              </div>
            );
          })
        }
      </Carousel>
    </div>
  );
};

export default PagesController;
