import React, { useState } from "react";
import axios from "axios";

interface IUserLoginInfo {
  name?: string;
  pass?: string;
}

const Auth = () => {
  const [isLoading, setIsLoading] = useState<Boolean>(false);
  const [inputData, setInputData] = useState<IUserLoginInfo>({
    name: "",
    pass: "",
  });

  const sendData = async () => {
    console.log(inputData);
    try {
      const response = await axios.post("localhost:4000/users/log_in", {
        name: inputData.name,
        password: inputData.pass,
      });
    } catch (error) {
      console.log(error);
    }
    console.log(response);
  };

  const handleFormSubmission = (e: any) => {
    setIsLoading(true);
    sendData();
    setIsLoading(false);
    e.preventDefault();
  };

  return (
    <div>
      <form onSubmit={handleFormSubmission}>
        <div id="u_name">
          <label htmlFor="name">Username:</label>
          <input
            name="name"
            type="text"
            onChange={(e) =>
              setInputData({
                name: e.target.value as string,
                pass: inputData.pass,
              })
            }
          />
        </div>

        <div id="u_pass">
          <label htmlFor="pass">Password</label>
          <input
            type="password"
            onChange={(e) =>
              setInputData({
                pass: e.target.value as string,
                name: inputData.name,
              })
            }
          />
        </div>

        <button type="submit">Log in</button>
      </form>
    </div>
  );
};

export default Auth;
