import Axios from "axios";
import React, { useEffect, useState } from "react";
import { ITest } from "../../@types/test";
import ActiveTests from "../../components/ActiveTests";
import { devURL } from "../../components/constants";
import Gallery from "../../components/Gallery";
import styles from "../styles/main.module.scss";
const main = () => {
  const getTestsByActive = async (isTestActive: boolean): Promise<any> => {
    try {
      const tests = await Axios.get(
        `http://localhost:4000/tests/getTest?active=${isTestActive}`
      );
      return tests.data as ITest[];
    } catch (error) {
      alert(error);
      return [];
    }
  };

  const [tests, setTests] = useState({
    activeTests: [],
    inActiveTests: []
  })

  const getAllTests = async () => {
    return await (await Axios.get(`${devURL}tests/allTests`)).data;
  };

  const filterTests = (activeTests, inActiveTests, arrToFilter) => {
    arrToFilter.forEach((test: ITest) => {
      test.active ? activeTests.push(test) : inActiveTests.push(test)
    });
  }

  useEffect(() => {
    const allTests = getAllTests();
    // console.log(allTests.then((value: any) => filterTests(activeTests, inActiveTests ,value)));
    let tmpactiveTests: Array<ITest>;
    let tmpinActiveTests: Array<ITest>;
    allTests.then((recievedTests: Array<ITest>) => filterTests(tmpactiveTests, tmpinActiveTests, recievedTests))
    setTests({
      activeTests: tmpactiveTests,
      inActiveTests: tmpinActiveTests
    })
  }, []);

  const toggleTestActiveState = async () => {
    try {
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className={styles.MainContainer}>
      <ActiveTests getActiveTests={getTestsByActive} />
      <Gallery getInactiveTests={getTestsByActive} />
    </div>
  );
};

export default main;
