import React from "react";
import LanguageBtn from "./LanguageBtn";
import compStyle from "./styles/LangBtnController.module.scss";

const LangBtnController = (props: {
  BtnArray: {
    map: (
      arg0: (
        button: string,
        key: string | number | null | undefined
      ) => (boolean | JSX.Element)[]
    ) => { btns: [JSX.Element, boolean] }[];
  };
  clickHandler: (btn: React.MouseEvent<HTMLButtonElement, MouseEvent>) => void;
  setIsNameEntryEnabled: any;
  active: string;
  langSelector: (chooseLanguage: any) => void;
}) => {
  type TBtnArrayWithPressedState = {
    btns: [JSX.Element, boolean];
  };

  const Btns: Array<TBtnArrayWithPressedState> = props.BtnArray.map(
    (button: string, key: string | number | null | undefined) => {
      return [
        <LanguageBtn
          btnID={button}
          btnText={button.toUpperCase()}
          handleBtnClick={props.clickHandler}
          inputEnabler={props.setIsNameEntryEnabled}
          pressed={button === props.active}
          key={key}
          langSelect={props.langSelector}
        />,
        button === props.active ? true : false,
      ];
    }
  );
  const ChangeBtnState = (btnID) => {
    Btns.forEach((element: any) => {
      console.log(element[0].props.btnID);
    });
  };
  console.log(Btns);
  ChangeBtnState(Btns);
  return (
    <div className={compStyle.LangSelect}>
      {Btns.map((ele: any) => {
        return ele;
      })}
    </div>
  );
};

export default LangBtnController;
