    ));

  return (
    <div className={styles.TestPreview}>
      <h3>Page: {props.activePage + 1}</h3>
      <div className={compStyles.TestBGContainer}>
        <FMLogo />
        <div className={compStyles.pageBody}>{pageBody()}</div>
      </div>
    </div>
  );
};

export default TestPreview;
