import React, { useState } from "react";
import compStyle from "./styles/LanguageBtn.module.scss";

const LanguageBtn = (props: {
  btnID: string;
  handleBtnClick: (
    btn: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ) => void;
  btnText: string;
  pressed: boolean;
}) => {
  const [isActive, setIsActive] = useState<boolean>(props.pressed);

  const testClick = (
    btn: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ): void => {
    btn.currentTarget.classList.toggle(compStyle.ButtonPressed);
  };

  return (
    <button
      id={props.btnID}
      onClick={testClick}
      className={`${compStyle.LangButton} ${
        isActive ? compStyle.ButtonPressed : null
      }`}
    >
      {props.btnText}
    </button>
  );
};

export default LanguageBtn;
