import React, { useEffect, useState } from "react";
import LanguageBtn from "./LanguageBtn";
import compStyle from "./styles/LangBtnController.module.scss";

const LangBtnController = (props) => {
  type TBtnArrayWithPressedState = {
    btns: [JSX.Element, boolean];
  };

  const [activeBtn, setActiveBtn] = useState<string>(props.active);
  /**
   * Sets currently active button.
   * @param id of a button
   */
  const activeBtnSetter = (id: string) => setActiveBtn(id);

  useEffect(() => {
    let flatBtns = Btns.flat().filter((ele) => typeof ele != "object");
    console.log(flatBtns);
    let tPosition = flatBtns.indexOf("true");

    if (props.isArrowClicked) {
      console.log(tPosition);
      props.arrowClicker(false);
    }
    Btns.forEach((button) => {
      console.log(button[0].props.btnID);
      if (button[0].props.btnID !== activeBtn) {
        button[1] = false;
      }
    });
    console.log(Btns);
  }, [activeBtn]);

  const Btns: Array<TBtnArrayWithPressedState> = props.BtnArray.map(
    (button, key) => {
      return [
        <LanguageBtn
          btnID={button}
          btnText={button.toUpperCase()}
          handleBtnClick={props.clickHandler}
          inputEnabler={props.setIsNameEntryEnabled}
          pressed={button === activeBtn ? true : false}
          key={key}
          langSelect={props.langSelector}
          setActiveBtn={activeBtnSetter}
        />,
        button === activeBtn ? true : false,
      ];
    }
  );

  return (
    <div className={compStyle.LangSelect}>
      {Btns.map((ele: any) => {
        return ele;
      })}
    </div>
  );
};

export default LangBtnController;
